import React from 'react';
import { useTheme } from '@material-ui/core/styles';
import Title from '../TitleComponent/index.js';
import Chart from 'chart.js';


function StockWatch(props){

console.log(props)

var barCount = 60;
var initialDateStr = '01 Apr 2017 00:00 Z';

var ctx = document.getElementById('chart').getContext('2d');
ctx.canvas.width = 240;
ctx.canvas.height = 240;
var myStockChart = new Chart(ctx, {
	type: 'candlestick',
	data: {
		datasets: [{
			label: 'CHRT - Chart.js Corporation',
			data: getRandomData(initialDateStr, barCount)
		}]
	},
	options: {
		scales: {
			x: {
				afterBuildTicks: function(scale) {
					const majorUnit = scale._majorUnit;
					const ticks = scale.ticks;
					const firstTick = ticks[0];
					let i, ilen, val, tick, currMajor, lastMajor;

					val = luxon.DateTime.fromMillis(ticks[0].value);
					if ((majorUnit === 'minute' && val.second === 0)
							|| (majorUnit === 'hour' && val.minute === 0)
							|| (majorUnit === 'day' && val.hour === 9)
							|| (majorUnit === 'month' && val.day <= 3 && val.weekday === 1)
							|| (majorUnit === 'year' && val.month === 0)) {
						firstTick.major = true;
					} else {
						firstTick.major = false;
					}
					lastMajor = val.get(majorUnit);

					for (i = 1, ilen = ticks.length; i < ilen; i++) {
						tick = ticks[i];
						val = luxon.DateTime.fromMillis(tick.value);
						currMajor = val.get(majorUnit);
						tick.major = currMajor !== lastMajor;
						lastMajor = currMajor;
					}
					return ticks;
				}
			}
		}
	}
});

